import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, NavLink } from 'react-router-dom';
import './border.css'


const TrainRoutes = () => {

    const [routes, setRoutes] = useState([])

    useEffect(() => {
        getRoutes();
    }, [])

    const getRoutes = () => {
        axios.get("http://localhost:8080/admin/route/get")
            .then(response => {
                setRoutes(response.data)
            }).catch(error => {
                console.log(error)
            })
    }


    const deleteRouteHandler = (routeId, e) => {
        axios.delete(`http://localhost:8080/admin/route/delete/${routeId}`)
            .then(response => {
                setRoutes(response.data)
            }).catch(error => {
                console.log(error)
            })
    }

    return (

        <div>
            {show ? <nav>
                <u id="nav">
                    <li><NavLink to="/addroutes">Add More Routes</NavLink></li>

                </u>
            </nav>: null}

            <table>
                <thead>
                    <tr>
                        <th>S.No</th>
                        <th>Route Id</th>
                        <th>SOURCE</th>
                        <th>DESTINATION</th>          
                        <th>Control</th>
                    </tr>
                </thead>

                <tbody>
                    {
                        routes.map((route, index) =>
                            <tr key={route.routeId}>
                                <td>{index + 1}</td>
                                <td> {route.routeId}</td>
                                <td> {route.source}</td>
                                <td> {route.destination}</td>
                                <td >
                                    <button onClick={() => deleteRouteHandler(route.routeId)}>Delete</button>
                                    <Link to={'/addroutes'} state={{ route: route }}><button >Update</button></Link>
                                </td>
                            </tr>

                        )
                    }

                </tbody>
            </table><br />
        </div>
    )
}

export default TrainRoutes;
