import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, NavLink } from 'react-router-dom';

const Stations = () => {

    const [station, setStation] = useState([])
    const [filterStation, setFilterStation] = useState([])
    const [searchStation, setSearchStation] = useState('')


    useEffect(() => {
        getStation();
    }, [])

    const getStation = () => {
        axios.get(`http://localhost:8080/admin/station/get`)
            .then(
                response => {
                    console.log(response.data)
                    setStation(response.data)
                    setFilterStation(response.data)
                }
            ).catch(
                error => {
                    console.log(error)
                }
            )
    }

    const deleteRouteHandler = (id, e) => {

        axios.delete(`http://localhost:8080/admin/station/delete/${id}`)
            .then(response => {
                console.log(response)
                setStation(response.data)

            }).catch(error => {
                console.log(error)
            })


    }
    const searchHandler = (e) => {
        if (e.target.value === '') {
            setStation(filterStation);
        }
        else {
            const filterResult = filterStation.filter(item => item.stationName.toLowerCase().includes(e.target.value.toLowerCase()))
            setStation(filterResult)
        }
        setSearchStation(e.target.value)
    }



    return (
        <div>
            <nav>
                <u id="nav">
                    <li><NavLink to="/addstation">Add More Station</NavLink></li>

                </u>
            </nav>
            <div>
                <label htmlFor='text'>Search</label><br></br>
                <input type='search' name='searchTrain' placeholder="searchByTrain" value={searchStation} onInput={(e) => searchHandler(e)} ></input>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>S.No</th>
                        <th>Station Id</th>
                        <th>Station Name</th>
                        <th>Delete</th>
                        <th>Update</th>
                    </tr>
                </thead>

                <tbody>
                    {
                        station.map((station, index) =>
                            <tr key={station.id}>
                                <td>{index + 1}</td>
                                <td>{station.id}</td>
                                <td>{station.stationName}</td>
                                <td>
                                    <button onClick={() => deleteRouteHandler(station.id)}>Delete</button></td>
                                <td>     <Link to={'/addstation'} state={{ station: station }} > <button >Update</button></Link> </td>
                            </tr>

                        )
                    }
                </tbody>
            </table><br />
            <Link to='/nav'>
                <button >back to Home page</button><br /><br />
            </Link>
        </div>
    )
}


export default Stations
