import axios from 'axios'
import React, { Component } from 'react'
import {  NavLink } from 'react-router-dom';

export class PostRoutes extends Component {
    constructor(props) {
        super(props);
        this.state = {
            source: '',
            destination: ''
        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = (e) => {

        axios.post('http://localhost:8080/admin/route/post', this.state)

            .then(response => {
                console.log(response.data)
            }).catch(error => {
                console.log(error)
            })

    }



    render() {
        const { source, destination } = this.state
        return (
            <div className='stylefrom'>
            <div className='forms' >
                <form onSubmit={(e) => this.submitHandler(e)}>
                    <h3>Add more Routes</h3>
                    <div>

                        < label htmlFor='text'> Source </label><br />
                        <input type="text" name="source"
                            value={source} onChange={this.changeHandler} required /><br /><br/>
                    </div>
                    <div>
                        <label htmlFor='text'>Destination </label><br />
                        <input type="text" name="destination" value={destination} onChange={this.changeHandler} required /><br /><br/>
                    </div>
                    <div>
                        <button  type="submit" >ADD</button><br /><br/>
                    </div>
                    <div>
                   <button><NavLink to={"/routes"} >back</NavLink></button> 
                   </div>
                </form>
            </div>
            </div>
        )
    }
}

export default PostRoutes
