import axios from 'axios'
import _ from 'lodash';
import React, { useEffect, useState } from 'react'
import { Link, useLocation, useNavigate } from 'react-router-dom';


const PostRoutes = () => {
    const navigate = useNavigate();
    const location = useLocation();

    const [routes, setRoutes] = useState({
        source: '',
        destination: ''
    })

    useEffect(() => {
        if (location.state?.route) {
            setRoutes(location.state.route)
        }

    }, [])


    const changeHandler = (e) => {
        setRoutes(prev => ({ ...prev, [e.target.name]: e.target.value }))
    }
    const submitHandler = (e) => {
        e.preventDefault()


        axios.post('http://localhost:8080/admin/route/post', routes)

            .then(response => {
                navigate('/routes')
            }).catch(error => {
                console.log(error)
            })

    }

    const [source , setSource] =useState([])
    useEffect(()=>{

        axios.get(`http://localhost:8080/admin/station/get`)
            .then(
                response => {
                    console.log(response.data)
                    setSource(response.data)
                }
            )
},[])


    return (
        <div className='stylefrom'>
            <div className='forms' >
                <form onSubmit={submitHandler}>
                    <h3>Add more Routes</h3>
                    <div>

                        < label htmlFor='text'> Source </label><br />
                        <select name='sourceName' id='source' placeholder=''>
                <option selected value={''}>select source</option>
                {_.map(source, s=>{<option key={s.id} value={s.id} >{s.source}</option>})}
                        </select>
                    </div>
                    <div>
                        <label htmlFor='text'>Destination </label><br />
                        <input type="text" name="destination" value={routes.destination} onChange={changeHandler} required /><br /><br />
                    </div>
                    <div>
                        <button type="submit" >{routes.id ? "Update" : "Save"}</button><br /><br />
                    </div>
                    <div>
                        <Link to={'/routes'}>   <button>back</button></Link>
                    </div>
                </form>
            </div>
        </div>
    )
}
export default PostRoutes
