import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, NavLink } from 'react-router-dom';

const GetTrain = () => {

  const [trains, setTrains] = useState([])
  const [order, setOrder] = useState("ASC")
  useEffect(() => {
    getRoutes();
  }, [])

  const getRoutes = () => {
    axios.get("http://localhost:8080/admin/traindetails/get")
      .then(response => {
        console.log(response.data)
        setTrains(response.data)
      })
      .catch(error => {
        console.log(error)
      })
  }


  const deleteRouteHandler = (id) => {
    axios.delete(`http://localhost:8080/admin/traindetails/delete/${id}`)
      .then(response => {
        console.log(response)
        setTrains(response.data)

      }).catch(error => {
        console.log(error)
      })


  }


  const ascending = (id) => {
    if (order == "ASC") {
      const numAscending = [...trains].sort((a, b) => a.id - b.id);
      console.log(numAscending);

      setTrains(numAscending);
    }
  }



  return (

    <div>
      <nav>
        <u id="nav">
          <li><NavLink to="/addtrains">Add More Trains</NavLink></li>

        </u>
      </nav>

      <table>
        <thead>
          <tr>
            <th>S.No</th>
            <th onClick={() => ascending("id")}>Train Id</th>
            <th>Train Number</th>
            <th>Train Name</th>
            <th>routeId</th>
            <th>Delete</th>
            <th>Update</th>
          </tr>
        </thead>

        <tbody>
          {
            trains.map((train, index) =>
              <tr key={train.id}>
                <td>{index + 1}</td>
                <td> {train.id}</td>
                <td> {train.trainNumber}</td>
                <td> {train.trainName}</td>

                <td> <button onClick={() => deleteRouteHandler(train.id)}>Delete</button></td>
                <td><Link to={'/addtrains'} state={{ train: train }} ><button>Update</button></Link> </td>
              </tr>
            )
          }

        </tbody>
      </table><br />
      <Link to='/nav'>
        <button >back to Home page</button><br /><br />
      </Link>
    </div>
  )
}


export default GetTrain
