import axios from 'axios';
import _ from 'lodash';
import React, { useEffect, useState } from 'react'
import { Link, NavLink } from 'react-router-dom';

const SearchTrain = () => {

    const [trains, setTrains] = useState([])

    const[filterSource,setfilterSource]=useState([])

    const[searchsource , setSearchsource]=useState([])

    useEffect(() => {
      getRoutes();
    }, [])

    const getRoutes = () => {
      axios.get("http://localhost:8080/admin/traindetails/get")
        .then(response => {
          console.log(response.data)
          setTrains(_.orderBy(response.data,"id"))
        })
        .catch(error => {
          console.log(error)
        })
    }


    const deleteRouteHandler = (id) => {
      axios.delete(`http://localhost:8080/admin/traindetails/delete/${id}`)
        .then(response => {
          console.log(response)
          setTrains(response.data)

        }).catch(error => {
          console.log(error)
        })


    }

    const searchHandler=(e)=>{

        const result=filterSource.filter(item=>item.route?.source.includes(e.target))

    }



    return (

      <div>
        <nav>
          <u id="nav">
            <li><NavLink to="/addtrains">Add More Trains</NavLink></li>

          </u>
        </nav>

<div>
    <label>search</label><br/>
    <input type={'search'} name='searchTrain' value={searchsource} onInput={searchHandler}></input><br/>

</div>

        <table>
          <thead>
            <tr>
              <th>S.No</th>
              <th>Train Id</th>
              <th>Train Number</th>
              <th>Train Name</th>
              <th>routeId</th>

            </tr>
          </thead>

          <tbody>
            {
              trains.map((train, index) =>
                <tr key={train.id}>
                  <td>{index + 1} </td>
                  <td> {train.id} </td>
                  <td> {train.trainNumber} </td>
                  <td> {train.trainName} </td>
                  <td> {train.route?.source} </td>
                  <td>{train.route?.destination}</td>

                </tr>
              )
            }

          </tbody>
        </table><br />
        <Link to='/nav'>
          <button >back to Home page</button><br /><br />
        </Link>
      </div>
    )
  }

export default SearchTrain
