import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, NavLink } from 'react-router-dom';

const GetrouteDetails = () => {

    const [routeDetails, setRouteDetails] = useState([])
    const [filterRouteDetails, setFilterRouteDetails] = useState([])
    const [searchTrain, setSearchTrain] = useState([])

    useEffect(() => {
        getRouteDetails();
    }, [])

    const getRouteDetails = () => {
        axios.get(`http://localhost:8080/admin/route-details/get`)
            .then(
                response => {
                    console.log(response.data)
                    setRouteDetails(response.data)
                    setFilterRouteDetails(response.data)
                }
            ).catch(
                error => {
                    console.log(error)
                }
            )
    }
    const deleteRouteHandler = (id) => {
        axios.delete(`http://localhost:8080/admin/route-details/delete/${id}`)
            .then(response => {
                console.log(response)
                setRouteDetails(response.data)
            }).catch(error => {
                console.log(error)
            })
    }


    const searchHandler = (e) => {
        if (e.target.value === '') {
            setRouteDetails(filterRouteDetails);
        } else {
            const filterResult = filterRouteDetails.filter((item => item.train.trainName.toLowerCase()
                .includes(e.target.value.toLowerCase()) || item.station.stationName.toLowerCase().includes(e.target.value.toLowerCase())));

            setRouteDetails(filterResult);
        }
        setSearchTrain(e.target.value);
    }


    return (
        <div>
            <nav>
                <u id="nav">
                    <li><NavLink to="/addroutedetails">Add More Station</NavLink></li>

                </u>
            </nav><br />

            <div className='SearchBar'>
                <label htmlFor='text'>Search By Train or Station Name</label><br /><br />
                <input className='SearchInput' type='search' name='searchTrain' placeholder="search...." value={searchTrain} onInput={(e) => searchHandler(e)} ></input><br /><br />
            </div>
            <table>
                <thead>
                    <tr>
                        <th>S.No</th>
                        <th>stationId</th>
                        <th>Train Name</th>
                        <th>Station Name</th>
                        <th>Time</th>
                        <th>Action</th>
                    </tr>
                </thead>

                <tbody>
                    {
                        routeDetails.map((routedetail, index) =>
                            <tr key={routedetail.id}>
                                <td>{index + 1}</td>
                            <td>{routeDetailss.train.route.routeId}</td>
                                <td>{routedetail?.train.trainName}</td>


                                <td>{routedetail?.station.stationName}</td>
                                <td>{routedetail.scheduleTime}</td>


                                <td>
                                    <button onClick={() => deleteRouteHandler(routedetail.id)}>Delete</button>
                                    <button >Update</button>
                                </td>
                            </tr>
                        )
                    }
                </tbody>
            </table><br />
            <Link to='/nav'>
                <button >back to Home page</button><br /><br />
            </Link>
        </div>
    )
}
export default GetrouteDetails
