import axios from 'axios';
import React, { Component } from 'react'
import { Link, NavLink } from 'react-router-dom';

export class Stations extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Stations: []
        }
    }

    componentDidMount() {
        axios.get(`http://localhost:8080/admin/station/get`)
            .then(
                response => {
                    console.log(response.data)
                    this.setState({ Stations: response.data })
                }
            ).catch(
                error => {
                    console.log(error)
                }
            )
    }



    render() {
        return (
            <div>
                <nav>
                    <u id="nav">
                        <li><NavLink to="/addstation">Add More Station</NavLink></li>

                    </u>
                </nav>

                <table>
                    <thead>
                        <tr>
                            <th>S.No</th>
                            <th>Station Id</th>
                            <th>Station Name</th>
                        </tr>
                    </thead>

                    <tbody>
                        {
                            this.state.Stations.map((station, index) =>
                                <tr key={station.id}>
                                    <td>{index + 1}</td>
                                    <td>{station.id}</td>
                                    <td>{station.stationName}</td>
                                    <td>
                                 <button onClick={()=>this.deleteRouteHandler(station.id)}>Delete</button>
                                 <button onClick={()=>this.updateRouteHandler(station.id)}>Update</button>
                            </td>
                                </tr>

                            )
                        }
                    </tbody>
                </table><br />
                <Link to='/nav'>
              <button >back to Home page</button><br /><br />
            </Link>
            </div>
        )
    }
}

export default Stations
