import axios from 'axios';
import React, { Component } from 'react'
import { Link, NavLink } from 'react-router-dom';

export class GetTrain extends Component {

    constructor(){
        super();
        this.state = {
            trains : []
        }
    }

    componentDidMount(){
      this.getRoutes();
    }

    getRoutes = () => {
        axios.get("http://localhost:8080/admin/traindetails/get")
        .then(response =>{

           console.log(response.data)
            this.setState({trains : response.data})
        })
        .catch(error => {
            console.log(error)
        })
    }


    deleteRouteHandler(id ,e ){
        axios.delete(`http://localhost:8080/admin/traindetails/delete/${id}`)
        .then(response => {
            console.log(response)
            this.setState({trains : response.data})
        }).catch(error => {
            console.log(error)
        })


    }



  render() {

    return (

      <div>
        <nav>    
        <u id="nav">  
          <li><NavLink to="/addtrains">Add More Trains</NavLink></li>

        </u>
        </nav>

        <table>
            <thead>
                <tr>
                    <th>S.No</th>
                    <th>Train Id</th>
                    <th>Train Number</th>
                    <th>Train Name</th>
                    <th>Control</th>
                </tr>
            </thead>

            <tbody >
                {
                    this.state.trains.map((train,index) =>
                        <tr key={train.id}>
                            <td>{index+1}</td>
                            <td> {train.id}</td>
                            <td> {train.trainNumber}</td>
                            <td> {train.trainName}</td>
                            <td>
                                 <button onClick={()=>this.deleteRouteHandler(train.id)}>Delete</button>
                              <Link to={'/addtrains/id'} ><button>Update</button></Link>
                            </td>
                        </tr>

                        )
                }

            </tbody>
        </table>
        <Link to='/nav'>
              <button >back to Home page</button><br /><br />
            </Link>
      </div>
    )
  }
}

export default GetTrain
