import axios from 'axios';
import React, { Component } from 'react'
import { Link } from 'react-router-dom';

export class PostStationDetails extends Component {
    constructor() {
        super();
        this.state = {

        }
    }
    changeHandler = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }

    clickHandler = (e) => {

        axios.post("http://localhost:8080/admin/station/post", this.state)
            .then(
                response => {
                    console.log(response.data)
                }
            ).catch(
                error => {
                    console.log(error)
                }
            )
    }


    render() {
        const { stationName } = this.state
        return (
            <div className='stylefrom'>
                <div className='forms' >
                    <form>
                        <h3>Add Stations</h3><br></br><br></br>
                        <div >
                            <label htmlFor='text'>Enter Station Name</label><br></br><br></br>
                            <input className='box' type={"time"} placeholder="station name.." name='stationName' value={stationName} onChange={this.changeHandler} required="true" ></input><br></br><br></br>
                        </div>
                        <div>
                            <button onClick={this.clickHandler}>-Add-</button><br></br><br></br>
                        </div>
                        <div>
                            <Link to={"/station"}> Back</Link>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}

export default PostStationDetails
