import axios from 'axios';
import React, { Component } from 'react'
import { Link, NavLink } from 'react-router-dom';

export class GetrouteDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            routeDetails: []
        }
    }

    componentDidMount() {
        axios.get(`http://localhost:8080/admin/route-details/get`)
            .then(
                response => {
                    console.log(response.data)
                    this.setState({ routeDetails: response.data })
                }
            ).catch(
                error => {
                    console.log(error)
                }
            )
    }

    deleteRouteHandler(id, e) {
        axios.delete(`http://localhost:8080/admin/route-details/delete/${id}`)
            .then(response => {
                console.log(response)
                this.setState({ routeDetails: response.data })
            }).catch(error => {
                console.log(error)
            })


    }


    render() {
        return (
            <div>
                <nav>
                    <u id="nav">
                        <li><NavLink to="/addroutedetails">Add More Station</NavLink></li>

                    </u>
                </nav>

                <table>
                    <thead>
                        <tr>
                            <th>S.No</th>
                            <th>Source</th>
                            <th>Destination</th>

                            <th>Station Name</th>
                            <th>Time</th>
                            <th>Action</th>
                        </tr>
                    </thead>

                    <tbody>
                        {
                            this.state.routeDetails.map((routedetail, index) =>
                                <tr key={routedetail.id}>
                                    <td>{routedetail.id}</td>
                                    <td>{routedetail.route.source}</td>
                                    <td>{routedetail.route.destination}</td>
                                    {/* <td>{routedetail.station.stationName}</td> */}
                                    <td>{routedetail.scheduleTime}</td>


                                    <td>
                                        <button onClick={() => this.deleteRouteHandler(routedetail.id)}>Delete</button>
                                        <button onClick={() => this.updateRouteHandler(routedetail.id)}>Update</button>
                                    </td>
                                </tr>

                            )
                        }
                    </tbody>
                </table><br />
                <Link to='/nav'>
                    <button >back to Home page</button><br /><br />
                </Link>
            </div>
        )
    }
}

export default GetrouteDetails
