import React, { Component } from 'react'

export class PostStationDetails extends Component {  
    constructor(){
        super();
        this.state={

        }
    }
    changeHandler=(e)=>{
        this.setState({[e.target.name]:e.target.value})
    } 

    clickHandler=(e)=>{
        e.preventDefault()
        axios.post("http://localhost:8080/admin/station/post" ,this.state)
        .then(
            response=>{
                console.log(response.data)
            }
        ).catch(
            error=>{
                console.log(error)
            }
        )
    }

  render() {
    const {stationName}=this.state
    return (
      <div>
        <form>
            <h3>Add Stations</h3>
        <div>
            <label htmlFor='text'>Enter Station Name</label><br></br>
            <input type={"text"} name='stationName' value={stationName}  onChange={this.changeHandler} required ></input>
        </div>
        <div>
            <button onClick={this.clickHandler}>-Add-</button>
        </div>
        </form>
      </div>
    )
  }
}

export default PostStationDetails
