import axios from 'axios';
import React, { Component } from 'react'
import { Link } from 'react-router-dom';

export class PostTrain extends Component {
    constructor(props) {
        super(props);
        this.state = {

        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = (e) => {
e.preventDefault();
        axios.post('http://localhost:8080/admin/traindetails/post', this.state)

            .then(response => {
                console.log(response.data)
            }).catch(error => {
                console.log(error)
            })

    }



    render() {
        const { trainNumber, trainName } = this.state
        return (
            <div className='stylefrom'>
            <div className='forms' >
                <form onSubmit={(e) => this.submitHandler(e)}>
                    <h3>Add more Routes</h3>
                    <div>

                        < label htmlFor='text'> Train Number </label><br />
                        <input type="text" name="trainNumber"
                            value={trainNumber} onChange={this.changeHandler} required /><br /><br/>
                    </div>
                    <div>
                        <label htmlFor='text'>Train Name </label><br />
                        <input type="text" name="trainName" value={trainName} onChange={this.changeHandler} required /><br /><br/>
                    </div>
                    <div>
                        <button  type="submit">ADD</button><br /><br/>
                    </div>
                    <div>
                   <button><Link to={"/trains"}>back</Link></button> 
                   </div>
                </form>
            </div>
            </div>
        )
    }
}

export default PostTrain
